SQLStoredProcedureSpec.CreateProcedureSQL:

create procedure DBO.USR_USP_INTERACTIONSENTIMENT_SAVE
  @ID            uniqueidentifier = null,
  @SENTIMENTTEXT nvarchar(100) = ''
as
  begin
      set NOCOUNT on;

      declare @CHANGEAGENTID uniqueidentifier;
      declare @CURRENTDATE datetime = GETDATE();

      if @CHANGEAGENTID is null
        exec DBO.USP_CHANGEAGENT_GETORCREATECHANGEAGENT
          @CHANGEAGENTID OUTPUT;

      begin TRY
          update [ATTRIBUTE46CBFF179A07471D8ADB4E5C38CFAB9A]
          set    [VALUE] = @SENTIMENTTEXT
          where  ID = @ID;

          if @@rowcount = 0
            insert into [dbo].[ATTRIBUTE46CBFF179A07471D8ADB4E5C38CFAB9A]
                        ([ID],
                         [VALUE],
                         [COMMENT],
                         [STARTDATE],
                         [ENDDATE],
                         [ADDEDBYID],
                         [CHANGEDBYID],
                         [DATEADDED],
                         [DATECHANGED])
            values      ( @ID,
                          @SENTIMENTTEXT,
                          '',
                          null,
                          null,
                          @CHANGEAGENTID,
                          @CHANGEAGENTID,
                          @CURRENTDATE,
                          @CURRENTDATE )
      end TRY
      begin CATCH
          exec DBO.USP_RAISE_ERROR
          return 1
      end CATCH

      return 0;
  end
