SQLStoredProcedureSpec.CreateProcedureSQL:
create procedure DBO.USR_USP_LOADCONTACTREPORTS ( @PROCESSID uniqueidentifier = null ) as begin SELECT I.ID,
STUFF(( SELECT ' ' + PROMPT FROM USR_GPTPROMPTS FOR XML PATH(''), TYPE).value('.', 'nvarchar(MAX)'), 1, 1, '') + i.OBJECTIVE AS CONTACTREPORT FROM [dbo].[INTERACTION] AS I WHERE I.ISCONTACTREPORT = 1 AND I.ID = 'FBBBBA05-7D0D-4898-A27B-57CB39705F6C';
end
